            <h2>Your Squad</h2>
            <div className="row">
                {squad.map((poke, index) => (
                    <div className="col-md-2 mb-4" key={index}>
                        <PokemonCard
                            name={poke.name}
                            image={poke.image}
                            types={poke.types}
                            type={poke.types[0]}
                            number={poke.number}
                            removeFromSquad={removeFromSquad}
                            inSquad={true}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Squad;
